                       FROM tbl_cont_catalogo_periodos
                      ORDER BY tbl_cont_catalogo_periodos.ano, tbl_cont_catalogo_periodos.mes
                     LIMIT 1)) AND tbl_bancos_cuentas_saldos.ano = (( SELECT tbl_cont_catalogo_periodos.ano
