            WHEN (( SELECT count(*) AS count
               FROM tbl_bancos_vs_ventas ce
              WHERE ce.id_entidadventa = p.id_entidadventa AND ce.id_tipobanco = c.tipo AND ce.id_clavebanco = c.clave)) = 0 THEN 0
