        CASE
            WHEN rc.id_cfd IS NULL THEN 0
            ELSE rc.id_cfd
        END AS id_cfd, 
        CASE
            WHEN rc.tfd IS NULL THEN 0
            ELSE rc.tfd::integer
        END AS tfd
   FROM tbl_ventas_facturas_cab rc
   JOIN tbl_vendedores v ON rc.id_vendedor = v.id_vendedor
