        END AS id_unidadsalida, rd.precio, rd.descuento, rd.iva, rd.ieps, rd.ivaret, rd.isrret, rd.obs, rd.importe, rd.importedesc, rd.importeiva, rd.importeieps, rd.importeivaret, rd.importeisrret, rd.totalpart, c.id_tipo
