		_result := 'ERROR: Ya existe un movimiento con esta clave';
	END IF;
	
	IF _err = 0
	THEN
		INSERT INTO TBL_NOM_VACACIONES
		VALUES( _ID_Vacaciones, _Desde, _Hasta, _Dias, _PV );
		
	END IF;
		
	RETURN QUERY SELECT _err, _result, _id_vacaciones;

END
$BODY$
