	if _Detallado = '0'
	then
		insert into _TMP_BALANCE(Clave,Concepto,Parcial,Total,Suma,AC)
		select substring(Cuenta,1,4), Nombre, 0, sum(Saldo), 0, '+' from view_cont_balance_general
