 SELECT rc.id_cierre, rc.clavedesde AS id_entidad, rc.numero, rc.fecha, rc.status, rc.nummovdesde AS desde, rc.nummovhasta AS hasta, rc.obs
   FROM tbl_ventas_cierres_cab rc;



